import { useState } from "react"
import "./ExpenseForm.css"

export default function ExpenseForm(props){

    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDate, setEnteredDate] = useState("");
    const [visibleForm, setVisibleForm] = useState(false);

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }

    const amountChangeHandler = (event) =>{
        setEnteredAmount(event.target.value);
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        };
        
        props.onSaveExpenseDate(expenseData);

        setEnteredTitle("")
        setEnteredAmount("")
        setEnteredDate("")
        setVisibleForm(true)
    };

    const addNewExpenseClicked = () => {
        setVisibleForm(false)
    }

    const onCancelClicked = () => {
        setVisibleForm(true)
    }

    let addNewExpense = (<div style={{textAlign:"center"}} className="new-expense__actions">
                                <button onClick={addNewExpenseClicked}>Add New Expense</button>
                        </div>)

    let formData = (<form onSubmit={submitHandler}>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" value={enteredTitle} onChange={titleChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" value={enteredAmount} onChange={amountChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2022-12-31" value={enteredDate} onChange={dateChangeHandler} />
            </div>
        </div>
        <div style={{display:"flex", justifyContent:"end"}}>
            <div className="new-expense__actions">
                <button onClick={onCancelClicked}>Cancel</button>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </div>
    </form>)

    if (visibleForm === false){
        return formData
    }else{
        return addNewExpense
    }

    
}
