import { useState } from "react";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import ExpenseItems from "./ExpenseItems";
import "./Expenses.css"

function Expenses(props){

    const [filteredYear, setFilteredYear] = useState("2020")

    const selectHandler = (selectedYear) => {
        setFilteredYear(selectedYear)
        console.log(filteredYear)
    }

    const filteredExpense = props.expenses.filter(expense => {
        return expense.date.getFullYear() === parseInt(filteredYear);
    })

    return (
        <div>
             
            <Card className="expenses">
                <ExpensesFilter selected={filteredYear} onSelectYear={selectHandler} />
                {filteredExpense.map((expenses) => (<ExpenseItems key={expenses.id} title={expenses.title} amount={expenses.amount} date={expenses.date} />))}   
            </Card>
        </div>
    );
}

export default Expenses;
