import { useState } from "react";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import ExpenseItems from "./ExpenseItems";
import "./Expenses.css"

function Expenses(props){

    const [filteredYear, setFilteredYear] = useState("2020")

    const selectHandler = (selectedYear) => {
        setFilteredYear(selectedYear)
        console.log(filteredYear)
    }

    function isSameYear(value){
        console.log(value.date.getFullYear())
        console.log(value.date.getFullYear() === filteredYear)
        console.log(value.date.getFullYear(),filteredYear)
        return value.date.getFullYear() === filteredYear;
    }

    const expenseByYear = props.expenses.filter(isSameYear)

    return (
        <div>
             
            <Card className="expenses">
                <ExpensesFilter selected={filteredYear} onSelectYear={selectHandler} />
                {expenseByYear.map((expenses) => (<ExpenseItems key={expenses.id} title={expenses.title} amount={expenses.amount} date={expenses.date} />))}   
            </Card>
        </div>
    );
}

export default Expenses;
